unsigned int pbkdf2_hmac_test_internal(HASH_ALG hash_alg, const unsigned char *pwd, unsigned short sp_key_idx, unsigned int pwd_bytes, const unsigned char *salt, unsigned int salt_bytes, unsigned int iter, const unsigned char *std_out, unsigned int std_out_bytes, const char *info)
{
    char         *name[] = {"SM3", "MD5", "SHA256", "SHA384", "SHA512", "SHA1", "SHA224", "SHA512_224", "SHA512_256"};
    unsigned char out[1000];
    unsigned int  ret;

    ret = pbkdf2_hmac(hash_alg, (unsigned char *)(size_t)pwd, sp_key_idx, pwd_bytes, (unsigned char *)(size_t)salt, salt_bytes, iter, out, std_out_bytes);
    if ((HASH_SUCCESS != ret) || memcmp_(out, (unsigned char *)(size_t)std_out, std_out_bytes)) {
        printf("\r\n PBKDF2-HMAC-%s test %s failure, ret=%x\r\n", name[hash_alg], info, ret);
        print_buf_U8((unsigned char *)(size_t)pwd, pwd_bytes, "password");
        print_buf_U8((unsigned char *)(size_t)salt, salt_bytes, "salt");
        printf("\r\n iteration counter = %u \r\n", iter);
        print_buf_U8(out, std_out_bytes, "out");
        return 1;
    } else {
        printf("\r\n PBKDF2-HMAC-%s test %s success", name[hash_alg], info);
        fflush(stdout);
        return 0;
    }
}

