unsigned int pbkdf2_speed_test_internal(HASH_ALG hash_alg)
{
    char *name[] = {
        "SM3",
        "MD5",
        "SHA256",
        "SHA384",
        "SHA512",
        "SHA1",
        "SHA224",
        "SHA512_224",
        "SHA512_256",
        "SHA3_224",
        "SHA3_256",
        "SHA3_384",
        "SHA3_512",
    };
    unsigned char pwd[16];
    unsigned char salt[16];
    unsigned int  pwd_bytes = 16, salt_bytes = 16;
    unsigned char out[16];
    unsigned int  out_bytes = 16;
    unsigned int  i;
    unsigned int  round = 10;
    unsigned int  ret;

    uint32_sleep(0xFFFFF, 1);

    printf("\r\n===================== PBDKF2-HMAC %s CPU begin =====================", name[hash_alg]);
    fflush(stdout);

    #ifdef PBKDF2_HMAC_SPEED_TEST_BY_TIMER
    round = 3;
    startP();
    #endif

    for (i = 0; i < round; i++) {
        ret = pbkdf2_hmac(hash_alg, pwd, 0, pwd_bytes, salt, salt_bytes, 5000, out, out_bytes);
        if (HASH_SUCCESS != ret) {
            printf("\r\n PBDKF2 error ret=%x", ret);
            fflush(stdout);
            return 1;
        }
    }

    #ifdef PBKDF2_HMAC_SPEED_TEST_BY_TIMER
    endP(1, 0, round);
    #else
    printf("\r\n finished");
    fflush(stdout);
    #endif

    return 0;
}

