    unsigned char         cipher[RSA_MAX_BYTE_LEN];
    unsigned char         plain1[RSA_MAX_BYTE_LEN], plain2[RSA_MAX_BYTE_LEN];
    rsa_crt_private_key_t rsa_crt_pri_key[1];
