/**
 * @brief           Comprehensive function to run all RSA OAEP tests
 * @return          0 if all tests pass, 1 if any test fails
 */
unsigned int rsaes_oaep_test(void)
{
    unsigned int ret;

    ret = rsaes_oaep_2048_std_test();
    if (ret) {
        return 1;
    }

    ret = rsaes_oaep_1024_rand_traversal_test();
    if (ret) {
        return 1;
    }

    return 0;
}

