#include <stdio.h>
#include "../app_config.h"

unsigned char eccp_get_key_test_internal(eccp_curve_t *curve, char *curve_name, unsigned char *prikey, unsigned char *pubkey, unsigned char ret_value, char *info)
{
    unsigned char buf[2 * ECCP_MAX_BYTE_LEN + 1] = {0};
    unsigned char ret;

    ret = eccp_get_pubkey_from_prikey(curve, prikey, buf);
    if ((ret_value != ret)) {
        printf("\r\n ECCp %s get key %s failure 1, ret=%d", curve_name, info, ret);
        return 1;
    }
    if ((PKE_SUCCESS == ret) && memcmp_(buf, pubkey, 2 * GET_BYTE_LEN(curve->eccp_p_bitLen))) {
        printf("\r\n ECCp %s get key %s failure 2, ret=%d", curve_name, info, ret);
        print_buf_U8(prikey, GET_BYTE_LEN(curve->eccp_p_bitLen), "prikey");
        print_buf_U8(buf, 2 * GET_BYTE_LEN(curve->eccp_p_bitLen), "pubkey");
        print_buf_U8(pubkey, 2 * GET_BYTE_LEN(curve->eccp_p_bitLen), "std pubkey");
        return 1;
    }

    printf("\r\n ECCp %s get key %s success", curve_name, info);

    return 0;
}

unsigned char eccp_get_key_test(eccp_curve_t *curve, char *curve_name, unsigned char *std_prikey, unsigned char *std_pubkey, unsigned char *pubkey_1, unsigned char *pubkey_2, unsigned char *pubkey_n_minus_2, unsigned char *pubkey_n_minus_1)
{
    //standard private key and public key
    unsigned char prikey[ECCP_MAX_BYTE_LEN] = {0};
    unsigned int  nByteLen                  = GET_BYTE_LEN(curve->eccp_n_bitLen);

    printf("\r\n -------------- %s get key test -------------- ", curve_name);

    /********** eccp get key standard data test **********/
    if (eccp_get_key_test_internal(curve, curve_name, std_prikey, std_pubkey, PKE_SUCCESS, "standard data test")) {
        return 1;
    }

    /********** prikey is 0 **********/
    memset_(prikey, 0, nByteLen);
    if (eccp_get_key_test_internal(curve, curve_name, prikey, NULL, PKE_ZERO_ALL, "test(prikey=0)")) {
        return 1;
    }

    /********** prikey is 1 **********/
    memset_(prikey, 0, nByteLen);
    prikey[nByteLen - 1] = 1;
    if (eccp_get_key_test_internal(curve, curve_name, prikey, pubkey_1, PKE_SUCCESS, "test(prikey=1)")) {
        return 1;
    }

    /********** prikey is 2 **********/
    memset_(prikey, 0, nByteLen);
    prikey[nByteLen - 1] = 2;
    if (eccp_get_key_test_internal(curve, curve_name, prikey, pubkey_2, PKE_SUCCESS, "test(prikey=2)")) {
        return 1;
    }

    /********** prikey is n-2 **********/
    reverse_byte_array((const unsigned char *)(curve->eccp_n), prikey, nByteLen);
    prikey[nByteLen - 1] -= 2;
    if (eccp_get_key_test_internal(curve, curve_name, prikey, pubkey_n_minus_2, PKE_SUCCESS, "test(prikey=n-2)")) {
        return 1;
    }

    /********** prikey is n-1 **********/
    reverse_byte_array((const unsigned char *)(curve->eccp_n), prikey, nByteLen);
    prikey[nByteLen - 1] -= 1;
    if (eccp_get_key_test_internal(curve, curve_name, prikey, pubkey_n_minus_1, PKE_SUCCESS, "test(prikey=n-1)")) {
        return 1;
    }

    /********** prikey is n **********/
    reverse_byte_array((const unsigned char *)(curve->eccp_n), prikey, nByteLen);
    if (eccp_get_key_test_internal(curve, curve_name, prikey, NULL, PKE_INTEGER_TOO_BIG, "test(prikey=n)")) {
        return 1;
    }

    /********** prikey is n+1 **********/
    reverse_byte_array((const unsigned char *)(curve->eccp_n), prikey, nByteLen);
    prikey[nByteLen - 1] += 1;
    if (eccp_get_key_test_internal(curve, curve_name, prikey, NULL, PKE_INTEGER_TOO_BIG, "test(prikey=n+1)")) {
        return 1;
    } else {
        printf("\r\n");
        return 0;
    }
}


